function lcs_recursive(text1: string, text2: string): number {
  let cache = {};

  function findLCS(s1, s2) {
    if (s1 > text1.length - 1 || s2 > text2.length - 1) return 0;
    let cacheKey = `${s1}:${s2}`;
    if (cache[cacheKey] !== undefined) {
      
      return cache[cacheKey];
    }
    
    let t1 = s1;
    let t2 = s2;

    let lcs = 0;
    if (text1[t1] === text2[t2]) {
      lcs = 1 + findLCS(t1 + 1, t2 + 1);
    } else {
      lcs = Math.max(
         t1 + 1 > text1.length-1 ? 0 : //avoid a call stack entry
        findLCS(t1 + 1, t2),
        t2 + 1 > text2.length-1 ? 0 :
        findLCS(t1, t2 + 1)
      );
    }
    cache[cacheKey] = lcs;
    
    return lcs;
  }

  return findLCS(0, 0);
}

